mapper.select:
select b.id ,a.name, brand_id ,brand_name, new_status, recommand_status, sale ,price, sub_title,stock,unit,weight,service_ids,detail_title,description,product_attribute_id,
product_attribute_value,product_attribute_name
from pms_product a ,pms_product_attribute_value b where a.id = b.product_id and a.id = #{id};
